import React, { useState, useContext, useEffect } from 'react';
import { TextField, Typography, Grid, Button, FormControl, InputLabel, Select, MenuItem } from '@material-ui/core';
import { v4 as uuidv4 } from 'uuid';
import Snackbar from '../../Snackbar/Snackbar';
import formatDate from '../../../utils/formatDate';
import { ExpenseTrackerContext } from '../../../context/context';
import { incomeCategories, expenseCategories } from '../../../constants/categories';
import useStyles from './styles';

const initialState = {
  amount: '',
  category: '',
  type: 'Income',
  date: formatDate(new Date()),
};

const NewTransactionForm = () => {
  const classes = useStyles();
  const { addTransaction } = useContext(ExpenseTrackerContext);
  const [formData, setFormData] = useState(initialState);
  const [open, setOpen] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [recognition, setRecognition] = useState(null);
  const [listening, setListening] = useState(false);

  const createTransaction = () => {
    if (Number.isNaN(Number(formData.amount)) || !formData.date.includes('-')) return;

    if (incomeCategories.map((iC) => iC.type).includes(formData.category)) {
      setFormData({ ...formData, type: 'Income' });
    } else if (expenseCategories.map((iC) => iC.type).includes(formData.category)) {
      setFormData({ ...formData, type: 'Expense' });
    }

    setOpen(true);
    addTransaction({ ...formData, amount: Number(formData.amount), id: uuidv4() });
    setFormData(initialState);
  };

  useEffect(() => {
    if (transcript) {
      const commands = transcript.split(' ');

      if (commands.includes('add') && commands.includes('expense')) {
        setFormData((prevData) => ({ ...prevData, type: 'Expense' }));
      } else if (commands.includes('add') && commands.includes('income')) {
        setFormData((prevData) => ({ ...prevData, type: 'Income' }));
      } else if (commands.includes('create') || commands.includes('finish') || commands.includes('save')) {
        createTransaction();
      } else if (commands.includes('cancel') || commands.includes('delete') || commands.includes('remove') || commands.includes('clear')) {
        setFormData(initialState);
      } else {
        commands.forEach((command, index) => {
          if (command === 'amount') {
            const amountIndex = index + 1;
            const amountValue = commands[amountIndex];
            if (!isNaN(amountValue)) {
              setFormData((prevData) => ({ ...prevData, amount: amountValue }));
            }
          } else if (command === 'category') {
            const categoryIndex = index + 1;
            const categoryValue = commands[categoryIndex];
            setFormData((prevData) => ({ ...prevData, category: categoryValue }));
          } else if (command === 'date') {
            const dateIndex = index + 1;
            const dateValue = commands.slice(dateIndex).join(' ');
            setFormData((prevData) => ({ ...prevData, date: formatDate(new Date(dateValue)) }));
          }
        });
      }
    }
  }, [transcript]);

  useEffect(() => {
    const speechRecognition = new window.webkitSpeechRecognition();
    speechRecognition.continuous = true;
    speechRecognition.interimResults = true;
    speechRecognition.lang = 'en-US';

    speechRecognition.onresult = (event) => {
      const speechToText = Array.from(event.results)
        .map((result) => result[0].transcript)
        .join('');
      setTranscript(speechToText);
    };

    setRecognition(speechRecognition);
  }, []);

  const startListening = () => {
    recognition.start();
    setListening(true);
  };

  const stopListening = () => {
    recognition.stop();
    setListening(false);
  };

  const selectedCategories = formData.type === 'Income' ? incomeCategories : expenseCategories;

  return (
    <Grid container spacing={2}>
      <Snackbar open={open} setOpen={setOpen} />
      <Grid item xs={12}>
        <Typography align="center" variant="subtitle2" gutterBottom>
          {transcript ? <div className="segment">{transcript}</div> : 'Start speaking to add a transaction...'}
        </Typography>
      </Grid>
      <Grid item xs={12} style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
        <Button variant="contained" color="primary" onClick={startListening} disabled={listening}>
          Start Listening
        </Button>
        <Button variant="contained" color="secondary" onClick={stopListening} disabled={!listening}>
          Stop Listening
        </Button>
      </Grid>
      <Grid item xs={6}>
        <FormControl fullWidth>
          <InputLabel>Type</InputLabel>
          <Select value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })}>
            <MenuItem value="Income">Income</MenuItem>
            <MenuItem value="Expense">Expense</MenuItem>
          </Select>
        </FormControl>
      </Grid>
      <Grid item xs={6}>
        <FormControl fullWidth>
          <InputLabel>Category</InputLabel>
          <Select value={formData.category} onChange={(e) => setFormData({ ...formData, category: e.target.value })}>
            {selectedCategories.map((c) => (
              <MenuItem key={c.type} value={c.type}>
                {c.type}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </Grid>
      <Grid item xs={6}>
        <TextField
          type="number"
          label="Amount"
          value={formData.amount}
          onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
          fullWidth
        />
      </Grid>
      <Grid item xs={6}>
        <TextField
          fullWidth
          label="Date"
          type="date"
          value={formData.date}
          onChange={(e) => setFormData({ ...formData, date: formatDate(e.target.value) })}
        />
      </Grid>
      <Button className={classes.button} variant="outlined" color="primary" fullWidth onClick={createTransaction}>
        Create
      </Button>
    </Grid>
  );
};

export default NewTransactionForm;
